/**
 * Escapes a few key HTML characters
 *
 * @param input
 * 		String to check and escape
 * @return 
 */
public static String encode(String input) {
    if (input == null)
        return input;

    String str = input.replaceAll("<", "&lt;");
    str = str.replaceAll(">", "&gt;");
    str = str.replaceAll("
", "<br />");
    return str;
}

public void testEscapeCharacters() throws Exception {
    assertEquals("&lt;tag&gt;", HtmlEncoder.encode("<tag>"));
    assertEquals("&lt;tag&gt;&lt;/tag&gt;", HtmlEncoder.encode("<tag></tag>"));
    assertEquals("<br />", HtmlEncoder.encode("
"));
    assertEquals("&lt;&lt;tag&gt;&gt;"Lots of text!"&lt;&lt;/tag&gt;&gt;", HtmlEncoder.encode("<<tag>>"Lots of text!"<</tag>>"));
}

