 */
public int compare(HCPDiagnosisBean a, HCPDiagnosisBean b) {
    int ret = 0;
    if ((a.getNumPatients()) > (b.getNumPatients()))
        ret = -1;
    else
        if ((a.getNumPatients()) < (b.getNumPatients()))
            ret = 1;


    return ret;
}

