private void addLoginFailure(int count, Timestamp lastFailure) throws SQLException {
    Connection conn = null;
    PreparedStatement ps = null;
    try {
        conn = factory.getConnection();
        ps = conn.prepareStatement(("INSERT INTO loginfailures(IPAddress,failureCount, lastFailure) " + "VALUES(?,?,?)"));
        ps.setString(1, ipAddr);
        ps.setInt(2, count);
        ps.setTimestamp(3, lastFailure);
        ps.executeUpdate();
    } catch (SQLException ex) {
        throw ex;
    } finally {
        DBUtil.closeConnection(conn, ps);
    }
}

