public ErrorList() {
    errorList = new ArrayList<String>();
}

/**
 * Adds a message to the list if its not a Java null or empty string.
 *
 * @param errorMessage
 * 		
 */
public void addIfNotNull(String errorMessage) {
    if ((errorMessage != null) && (!("".equals(errorMessage))))
        errorList.add(errorMessage);

}

/**
 * Returns the list of error messages
 *
 * @return 
 */
public List<String> getMessageList() {
    return errorList;
}

/**
 * Returns true if the list has any errors
 *
 * @return 
 */
public boolean hasErrors() {
    return (errorList.size()) != 0;
}

public Iterator<String> iterator() {
    return errorList.iterator();
}

@Override
public String toString() {
    return errorList.toString();
}

/**
 * Constructor with error messages passed as a list of parameters to the method.
 *
 * @param errorMessages
 * 		The list of error messages to be returned in the special form validation box.
 */
public FormValidationException(String... errorMessages) {
    errorList = new ErrorList();
    for (String msg : errorMessages) {
        errorList.addIfNotNull(msg);
    }
}

/**
 * Constructor with error messages as a special ErrorList data type.
 *
 * @param errorList
 * 		An ErrorList object which contains the list of error messages.
 */
public FormValidationException(ErrorList errorList) {
    this.errorList = errorList;
}

/**
 * Returns the error list as a java.util.List of Strings.
 *
 * @return The error list
 */
public List<String> getErrorList() {
    return errorList.getMessageList();
}

/**
 * The error message will be displayed at the top of the iTrust page as in other iTrust Exceptions.
 */
@Override
public String getMessage() {
    return "This form has not been validated correctly. The following field are not properly filled in: " + (errorList.toString());
}

/**
 * The special formatting for error messages is then kept in one place.
 *
 * @param out
 * 		The output writer (in this case a JSPWriter) where the formatted list will go.
 * @throws IOException
 * 		If the writer is incorrect.
 */
public void printHTML(JspWriter out) throws IOException {
    out.print("<h2>Information not valid</h2><div class="errorList">");
    for (String errorMessage : errorList) {
        out.print(((StringEscapeUtils.escapeHtml(errorMessage)) + "<br />"));
    }
    out.print("</div>");
}

/**
 * Like printHTML, except a string is returned.
 */
public String printHTMLasString() {
    String r = "<h2>Information not valid</h2><div class="errorList">";
    for (String errorMessage : errorList) {
        r += (StringEscapeUtils.escapeHtml(errorMessage)) + "<br />";
    }
    r += "</div>";
    return r;
}

public void testMessage() throws Exception {
    ErrorList el = new ErrorList();
    el.addIfNotNull("a");
    FormValidationException e = new FormValidationException(el);
    assertEquals(("This form has not been validated correctly. " + "The following field are not properly filled in: [a]"), e.getMessage());
    MockJSPWriter writer = new MockJSPWriter();
    e.printHTML(writer);
    assertEquals("<h2>Information not valid</h2><div class="errorList">a<br /></div>", writer.input);
    assertEquals("<h2>Information not valid</h2><div class="errorList">a<br /></div>", e.printHTMLasString());
}

@Override
public void print(String arg0) throws IOException {
    this.input += arg0;
}

