    wr = wr.getLinkWith("Echovirus(79.10)").click();
    WebTable table = wr.getTableStartingWithPrefix("HCP");
    assertTrue(table.getCellAsText(1, 0).contains("Jason"));
    assertTrue(table.getCellAsText(1, 1).contains("2"));
