 _hashtable.put(hashKey, ply);\r\n    // Append the new ply at the end of the list of plies.\r\n    // ATTENTION: this will only work, if theres only _one_\r\n    // ply for each key!\r\n    _orderedList.add(hashKey);\r\n}'),(267,'removeOldestEntry()','de.java_chess.javaChess.engine.hashtable.PlyHashtableImpl.removeOldestEntry()','29','PlyHashtableImpl','/**\r\n * Try to remove the oldest entry.\r\n * ATTENTION: this version will only work, if theres only _one_ entry for each hashkey!\r\n */\r\npublic final void removeOldestEntry() {\r\n    // Get and remove the oldest ply from the ordered list.\r\n    _hashtable.remove(_orderedList.removeFirst());\r\n}'),(268,'reset()','de.java_chess.javaChess.engine.hashtable.PlyHashtableImpl.reset()','29','PlyHashtableImpl','// Methods\r\n/**\r\n * Reset the hashtables for a new game.\r\n */\r\npublic void reset() {\r\n    _hashtable.clear();\r\n    // Remove all the entries from the hashtable.\r\n    _orderedList.clear();// And the ordered list.\r\n\r\n}'),(269,'setMaximumSize(int)','de.java_chess.javaChess.engine.hashtable.PlyHashtableImpl.setMaximumSize(int)','29','PlyHashtableImpl','/**\r\n * Set the maximum number of entries in the hashtable.\r\n *\r\n * @param maximumEntries\r\n * 		The new maximum number of entries.\r\n */\r\npublic final void setMaximumSize(int maximumEntries) {\r\n    _maxSize = maximumEntries;\r\n    // Remove the oldest entries, if the current size\r\n    // is bigger than the new maximum size.\r\n    while ((getSize()) > (getMaximumSize())) {\r\n        removeOldestEntry();\r\n    } \r\n}'),(270,'-init-()','de.java_chess.javaChess.engine.hashtable.ZobristKeyImpl.-init-()','30','ZobristKeyImpl','// Instance variables\r\n// Constructors\r\n/**\r\n * Create a new key instance.\r\n */\r\nprivate ZobristKeyImpl() {\r\n    // Create a new multidimensional array.\r\n    _factors = new long[2][6][64];\r\n    Random rand = new Random();\r\n    for (int i = 0; i < 64; i++) {\r\n        for (int j = 0; j < 6; j++) {\r\n            _factors[0][j][i] = rand.nextLong();\r\n            _factors[1][j][i] = rand.nextLong();\r\n        }\r\n    }\r\n    _blackMoves = rand.nextLong();\r\n}'),(271,'computeKey(de.java_chess.javaChess.bitboard.BitBoard,boolean)','de.java_chess.javaChess.engine.hashtable.ZobristKeyImpl.computeKey(de.java_chess.javaChess.bitboard.BitBoard,boolean)','30','ZobristKeyImpl','/**\r\n * Compute a key for a board and a color.\r\n *\r\n * @param board\r\n * 		The current board.\r\n * @param white\r\n * 		Flag to indicate if white moves.\r\n * @return The zobrist key.\r\n */\r\npublic long computeKey(BitBoard board, boolean white) {\r\n    long val = 0L;\r\n    // Get a bitmask of the empty squares to speedup the\r\n    // test for a piece on the square.\r\n    long emptySquareMask = board.getEmptySquares();\r\n    // Reuse the same position object for each square.\r\n    Position curPosition = new PositionImpl(0);\r\n    for (int square = 0; square < 64; square++) {\r\n        if ((emptySquareMask & 1) == 0) {\r\n            // If theres a piece on the square\r\n            curPosition.setSquareIndex(square);\r\n            Piece p = board.getPiece(curPosition);\r\n            if (p != null) {\r\n                val ^= _factors[p.getColor()][((p.getType()) - 1)][square];\r\n            }\r\n        }\r\n        emptySquareMask >>>= 1;// Shift the mask to test for the next square.\r\n\r\n    }\r\n    if (!white) {\r\n        val ^= _blackMoves;\r\n    }\r\n    return val;\r\n}'),(272,'getInstance()','de.java_chess.javaChess.engine.hashtable.ZobristKeyImpl.getInstance()','30','ZobristKeyImpl','// Methods\r\n/**\r\n * Get the only instance of this class.\r\n */\r\npublic static final ZobristKeyImpl getInstance() {\r\n    if ((ZobristKeyImpl._instance) == null) {\r\n        ZobristKeyImpl._instance = new ZobristKeyImpl();\r\n    }\r\n    return ZobristKeyImpl._instance;\r\n}'),(273,'addPGNopening(java.io.File)','de.java_chess.javaChess.engine.opening_book.OpeningB