npublic String addRepresentative(String pidString) throws iTrustException {\r\n    try {\r\n        long representee = Long.valueOf(pidString);\r\n        if ((authDAO.getUserRole(representee)) != (Role.PATIENT))\r\n            throw new iTrustException(\"This user is not a patient!\");\r\n        else\r\n            if ((super.pid) == representee)\r\n                throw new iTrustException(\"This user cannot represent themselves.\");\r\n\r\n\r\n        boolean confirm = patientDAO.addRepresentative(pid, representee);\r\n        if (confirm) {\r\n            return \"Patient represented\";\r\n        } else\r\n            return \"No change made\";\r\n\r\n    } catch (NumberFormatException e) {\r\n        return \"MID not a number\";\r\n    }\r\n}'),(508,'getRepresented(long)','edu.ncsu.csc.itrust.action.EditRepresentativesAction.getRepresented(long)','94','edu.ncsu.csc.itrust.action.EditRepresentativesAction','/**\r\n * Return a list of patients that pid represents\r\n *\r\n * @param pid\r\n * 		The id of the personnel we are looking up representees for.\r\n * @return a list of PatientBeans\r\n * @throws iTrustException\r\n * 		\r\n */\r\npublic List<PatientBean> getRepresented(long pid) throws iTrustException {\r\n    return patientDAO.getRepresented(pid);\r\n}'),(509,'removeRepresentative(java.lang.String)','edu.ncsu.csc.itrust.action.EditRepresentativesAction.removeRepresentative(java.lang.String)','94','edu.ncsu.csc.itrust.action.EditRepresentativesAction','/**\r\n * Makes the patient (pid) no longer represent the input mid param\r\n *\r\n * @param input\r\n * 		the mid of the person be represented (representee)\r\n * @return a message\r\n * @throws iTrustException\r\n * 		\r\n */\r\npublic String removeRepresentative(String input) throws iTrustException {\r\n    try {\r\n        long representee = Long.valueOf(input);\r\n        boolean confirm = patientDAO.removeRepresentative(pid, representee);\r\n        if (confirm) {\r\n            return \"Patient represented\";\r\n        } else\r\n            return \"No change made\";\r\n\r\n    } catch (NumberFormatException e) {\r\n        return \"MID not a number\";\r\n    }\r\n}'),(510,'-init-()','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest.-init-()','95','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest','EditRepresentativesActionTest() {\r\n}'),(511,'setUp()','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest.setUp()','95','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest','@Override\r\nprotected void setUp() throws Exception {\r\n    gen.clearAllTables();\r\n    gen.patient1();// 2 represents 1, but not 4\r\n\r\n    gen.patient2();\r\n    gen.patient4();\r\n}'),(512,'testAddNotNumber()','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest.testAddNotNumber()','95','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest','public void testAddNotNumber() throws Exception {\r\n    action = new EditRepresentativesAction(factory, 9000000000L, \"2\");\r\n    assertEquals(\"MID not a number\", action.addRepresentative(\"a\"));\r\n    assertEquals(\"MID not a number\", action.removeRepresentative(\"a\"));\r\n}'),(513,'testAddRepresentative()','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest.testAddRepresentative()','95','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest','public void testAddRepresentative() throws Exception {\r\n    action = new EditRepresentativesAction(factory, 9000000000L, \"2\");\r\n    action.addRepresentative(\"4\");\r\n    List<PatientBean> reps = action.getRepresented(2);\r\n    assertEquals(2, reps.size());\r\n    assertEquals(4L, reps.get(1).getMID());\r\n}'),(514,'testCannotRepresentSelf()','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest.testCannotRepresentSelf()','95','edu.ncsu.csc.itrust.action.EditRepresentativesActionTest','public void testCannotRepresentSelf() throws Exception {\r\n    action = new EditRepresentativesAction(factory, 9000000000L, \"2\");\r\n    try {\r\n        a