 throws Exception {\r\n    action = new EditRepresentativesAction(factory, 9000000000L, \"2\");\r\n    action.removeRepresentative(\"1\");\r\n    List<PatientBean> reps = action.getRepresented(2);\r\n    assertEquals(0, reps.size());\r\n}'),(519,'-init-(edu.ncsu.csc.itrust.dao.DAOFactory,long)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.-init-(edu.ncsu.csc.itrust.dao.DAOFactory,long)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','public EditSingleReferralAction(DAOFactory factory, long hcpid) throws iTrustException {\r\n    referralDAO = factory.getReferralDAO();\r\n    patientDAO = factory.getPatientDAO();\r\n    personnelDAO = factory.getPersonnelDAO();\r\n    ovDAO = factory.getOfficeVisitDAO();\r\n    messageAction = new SendMessageAction(factory, hcpid);\r\n    this.hcpid = hcpid;\r\n}'),(520,'addReferral(edu.ncsu.csc.itrust.beans.ReferralBean)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.addReferral(edu.ncsu.csc.itrust.beans.ReferralBean)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','/**\r\n * Add a new referral to the database.\r\n *\r\n * @param bean\r\n * 		\r\n * @throws Exception\r\n * 		\r\n */\r\npublic void addReferral(ReferralBean bean) throws Exception {\r\n    if ((bean.getSenderID()) != (hcpid)) {\r\n        throw new iTrustException(\"New referrals must be from the current HCP.\");\r\n    }\r\n    long id = referralDAO.addReferral(bean);\r\n    bean = referralDAO.getReferral(id);\r\n    sendReferralCreatedMessages(bean);\r\n}'),(521,'deleteReferral(edu.ncsu.csc.itrust.beans.ReferralBean)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.deleteReferral(edu.ncsu.csc.itrust.beans.ReferralBean)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','/**\r\n * Delete a referral from the database.\r\n *\r\n * @param bean\r\n * 		\r\n * @throws Exception\r\n * 		\r\n */\r\npublic void deleteReferral(ReferralBean bean) throws Exception {\r\n    if ((bean.getSenderID()) != (hcpid)) {\r\n        throw new iTrustException(\"Deleted referrals must be from the current HCP.\");\r\n    }\r\n    referralDAO.removeReferral(bean.getId());\r\n    sendReferralCancelledMessages(bean);\r\n}'),(522,'editReferral(edu.ncsu.csc.itrust.beans.ReferralBean)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.editReferral(edu.ncsu.csc.itrust.beans.ReferralBean)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','/**\r\n * Edit an existing referral.\r\n *\r\n * @param bean\r\n * 		\r\n * @throws iTrustException\r\n * 		\r\n */\r\npublic void editReferral(ReferralBean bean) throws iTrustException {\r\n    if ((bean.getSenderID()) != (hcpid)) {\r\n        throw new iTrustException(\"Edited referrals must be from the current HCP.\");\r\n    }\r\n    referralDAO.editReferral(bean);\r\n}'),(523,'getOfficeVisitDate(edu.ncsu.csc.itrust.beans.ReferralBean)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.getOfficeVisitDate(edu.ncsu.csc.itrust.beans.ReferralBean)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','public String getOfficeVisitDate(ReferralBean bean) throws iTrustException {\r\n    return ovDAO.getOfficeVisit(bean.getOvid()).getVisitDateStr();\r\n}'),(524,'getPatientName(edu.ncsu.csc.itrust.beans.ReferralBean)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.getPatientName(edu.ncsu.csc.itrust.beans.ReferralBean)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','/**\r\n * Get the patient name associated with the given referral.\r\n *\r\n * @param bean\r\n * 		\r\n * @return The patients name as a String.\r\n * @throws iTrustException\r\n * 		\r\n */\r\npublic String getPatientName(ReferralBean bean) throws iTrustException {\r\n    return patientDAO.getName(bean.getPatientID());\r\n}'),(525,'getReceivingHCPName(edu.ncsu.csc.itrust.beans.ReferralBean)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.getReceivingHCPName(edu.ncsu.csc.itrust.beans.ReferralBean)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','/**\r\n * Get the name of the receiving HCP associated with the given referral.\r\n *\r\n * @param bean\r\n * 		\r\n * @return The HCPs name as a String.\r\n * @throws iTrustException\r\n * 		\r\n */\r\npublic String getReceivingHCPName(ReferralBean bean) throws iTrustException {\r\n    return personnelDAO.getName(bean.getReceiverID());\r\n}'),(526,'getReceivingHCPSpecialty(edu.ncsu.csc.itrust.beans.ReferralBean)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.getReceivingHCPSpecialty(edu.ncsu.csc.itrust.beans.ReferralBean)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','public String getReceivingHCPSpecialty(ReferralBean bean) throws iTrustException {\r\n    String s = personnelDAO.getPersonnel(bean.getReceiverID()).getSpecialty();\r\n    return s == null ? \"\" : s;\r\n}'),(527,'getReferral(long)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.getReferral(long)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','/**\r\n * Get a specific referral.\r\n *\r\n * @param id\r\n * 		\r\n * @return \r\n * @throws DBException\r\n * 		\r\n */\r\npublic ReferralBean getReferral(long id) throws DBException {\r\n    return referralDAO.getReferral(id);\r\n}'),(528,'sendReferralCancelledMessages(edu.ncsu.csc.itrust.beans.ReferralBean)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.sendReferralCancelledMessages(edu.ncsu.csc.itrust.beans.ReferralBean)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','/**\r\n * Send messages to the users associated with canceling a referral.\r\n *\r\n * @param bean\r\n * 		\r\n * @throws Exception\r\n * 		\r\n */\r\npublic void sendReferralCancelledMessages(ReferralBean bean) throws Exception {\r\n    StringBuilder body = new StringBuilder();\r\n    body.append(((\"The referral created on \" + (bean.getTimeStamp())) + \"has been deleted.\n\n\"));\r\n    body.append(\"The referral is reproduced here for your convienence:\n\");\r\n    body.append(((((\"Sending HCP: \" + (personnelDAO.getName(bean.getSenderID()))) + \" (\") + (personnelDAO.getPersonnel(bean.getSenderID()).getSpecialty())) + \")\n\"));\r\n    body.append(((((\"Receiving HCP: \" + (personnelDAO.getName(bean.getReceiverID()))) + \" (\") + (personnelDAO.getPersonnel(bean.getReceiverID()).getSpecialty())) + \")\n\"));\r\n    body.append(((\"Patient: \" + (getPatientName(bean))) + \"\n\"));\r\n    body.append(((\"Notes: \" + (bean.getReferralDetails())) + \"\n\"));\r\n    body.append(((\"Created on: \" + (bean.getTimeStamp())) + \"\n\"));\r\n    MessageBean receiverMsg = new MessageBean();\r\n    receiverMsg.setFrom(bean.getSenderID());\r\n    receiverMsg.setTo(bean.getReceiverID());\r\n    receiverMsg.setBody(body.toString());\r\n    receiverMsg.setSubject(\"Your Referral Was Cancelled\");\r\n    receiverMsg.setRead(0);\r\n    messageAction.sendMessage(receiverMsg);\r\n    MessageBean patientMsg = new MessageBean();\r\n    patientMsg.setFrom(bean.getSenderID());\r\n    patientMsg.setTo(bean.getPatientID());\r\n    patientMsg.setBody(body.toString());\r\n    patientMsg.setSubject(\"Your Referral Was Cancelled\");\r\n    patientMsg.setRead(0);\r\n    messageAction.sendMessage(patientMsg);\r\n}'),(529,'sendReferralCreatedMessages(edu.ncsu.csc.itrust.beans.ReferralBean)','edu.ncsu.csc.itrust.action.EditSingleReferralAction.sendReferralCreatedMessages(edu.ncsu.csc.itrust.beans.ReferralBean)','96','edu.ncsu.csc.itrust.action.EditSingleReferralAction','/**\r\n * Send messages to the users associated with creating a referral.\r\n *\r\n * @param bean\r\n * 		\r\n * @throws Exception\r\n * 		\r\n */\r\npublic void sendReferralCreatedMessages(ReferralBean bean) throws Exception {\r\n    StringBuilder body = new StringBuilder();\r\n    body.append(\"Referral information:\n\");\r\n    String senderSpecialty = personnelDAO.getPersonnel(bean.getSenderID()).getSpecialty();\r\n    String receiverSpecialty = personnelDAO.getPersonnel(bean.getReceiverID()).getSpecialty();\r\n    senderSpecialty = (senderSpecialty == null) ? \"no spec