r\n * @return the persons name\r\n * @throws DBException\r\n * 		\r\n * @throws iTrustException\r\n * 		\r\n */\r\npublic String getUserName(String inputMID) throws iTrustException {\r\n    try {\r\n        long mid = Long.valueOf(inputMID);\r\n        return factory.getAuthDAO().getUserName(mid);\r\n    } catch (NumberFormatException e) {\r\n        throw new iTrustException(\"MID not in correct form\");\r\n    }\r\n}'),(580,'-init-()','edu.ncsu.csc.itrust.action.GetUserNameActionTest.-init-()','107','edu.ncsu.csc.itrust.action.GetUserNameActionTest','GetUserNameActionTest() {\r\n}'),(581,'setUp()','edu.ncsu.csc.itrust.action.GetUserNameActionTest.setUp()','107','edu.ncsu.csc.itrust.action.GetUserNameActionTest','@Override\r\nprotected void setUp() throws Exception {\r\n    gen.clearAllTables();\r\n}'),(582,'testCorrectFormat()','edu.ncsu.csc.itrust.action.GetUserNameActionTest.testCorrectFormat()','107','edu.ncsu.csc.itrust.action.GetUserNameActionTest','public void testCorrectFormat() throws Exception {\r\n    gen.hcp0();\r\n    assertEquals(\"Kelly Doctor\", new GetUserNameAction(factory).getUserName(\"9000000000\"));\r\n}'),(583,'testWrongFormat()','edu.ncsu.csc.itrust.action.GetUserNameActionTest.testWrongFormat()','107','edu.ncsu.csc.itrust.action.GetUserNameActionTest','public void testWrongFormat() throws Exception {\r\n    gen.hcp0();\r\n    try {\r\n        new GetUserNameAction(factory).getUserName(\"90000000aaa01\");\r\n        fail(\"Exception should have been thrown\");\r\n    } catch (iTrustException e) {\r\n        assertEquals(\"MID not in correct form\", e.getMessage());\r\n    }\r\n}'),(584,'-init-(java.lang.String)','edu.ncsu.csc.itrust.action.GetVisitRemindersAction.-init-(java.lang.String)','109','edu.ncsu.csc.itrust.action.GetVisitRemindersAction','private ReminderType(String typeName) {\r\n    this.typeName = typeName;\r\n}'),(585,'-init-(edu.ncsu.csc.itrust.dao.DAOFactory,long)','edu.ncsu.csc.itrust.action.GetVisitRemindersAction.-init-(edu.ncsu.csc.itrust.dao.DAOFactory,long)','109','edu.ncsu.csc.itrust.action.GetVisitRemindersAction','/**\r\n * Set up defaults\r\n *\r\n * @param factory\r\n * 		The DAOFactory used to create the DAOs used in this action.\r\n * @param loggedInMID\r\n * 		MID of the person who is logged in\r\n * @throws iTrustException\r\n * 		\r\n */\r\npublic GetVisitRemindersAction(DAOFactory factory, long loggedInMID) throws iTrustException {\r\n    this.loggedInMID = loggedInMID;\r\n    visitReminderDAO = factory.getVisitRemindersDAO();\r\n    patientDAO = factory.getPatientDAO();\r\n}'),(586,'checkImmunizations(long)','edu.ncsu.csc.itrust.action.GetVisitRemindersAction.checkImmunizations(long)','109','edu.ncsu.csc.itrust.action.GetVisitRemindersAction','/**\r\n * Checks a patient to see what immunizations they need\r\n *\r\n * @param pid\r\n * 		patient to be checked\r\n * @return patient list of those lacking immunizations according to the schedule\r\n */\r\nprivate String checkImmunizations(long pid) throws iTrustException {\r\n    String reason = \"\";\r\n    List<ProcedureBean> procs = patientDAO.getProcedures(pid);\r\n    PatientBean patient = patientDAO.getPatient(pid);\r\n    // long patientAge = patient.getAgeInWeeks();\r\n    Date patientDOB = patient.getDateOfBirth();\r\n    Gender gen = patient.getGender();\r\n    int hepB = 0;\r\n    long hepBTime = 0;\r\n    int rota = 0;\r\n    long rotaTime = 0;\r\n    int diptet = 0;\r\n    long deptetTime = 0;\r\n    int haemoflu = 0;\r\n    long haemofluTime = 0;\r\n    long haemofluTimeFirst = 0;\r\n    int pneumo = 0;\r\n    long pneumoTime = 0;\r\n    long pneumofluTimeFirst = 0;\r\n    int polio = 0;\r\n    long polioTime = 0;\r\n    int measles = 0;\r\n    long measlesTime = 0;\r\n    int varicella = 0;\r\n    long varicellaTime = 0;\r\n    int hepA = 0;\r\n    long hepATime = 0;\r\n    int hpv = 0;\r\n    long hpvTime = 0;\r\n    for (ProcedureBean proc : procs) {\r\n        String cpt = proc.getCPTCode();\r\n        // Hep B (90371)\r\n        if (cpt.equals(\"90371\")) {\r\n            hepB++;\r\n            hepBTime = proc.getDate().getTime();\r\n        } else// Rotavirus (90681)\r\n\r\n            if (cpt.equals(\"90681\")) {\r\n                rota++;\r\n                rotaTime = proc.getDate().getTime();\r\n            } else// Diptheria, Tetanus, Pertussis (90696)\r\n\r\n                if (cpt.equals(\"90696\")) {\r\n                    diptet++;\r\n                    deptetTime = proc.getDate().getTime();\r\n                } else// Haemophilus influenza (90645)\r\n\r\n                    if (cpt.equals(\"90645\")) {\r\n                        if (0 == haemoflu)\r\n                            haemofluTimeFirst = proc.getDate().getTime();\r\n\r\n                        haemoflu++;\r\n                        haemofluTime = proc.getDate().getTime();\r\n                    } else// Pneumococcal (90669)\r\n\r\n                        if (cpt.equals(\"90669\")) {\r\n                            if (0 == pneumo)\r\n                                pneumofluTimeFirst = proc.getDate().getTime();\r\n\r\n                            pneumo++;\r\n                            pneumoTime = proc.getDate().getTime();\r\n                        } else// Poliovirus (90712)\r\n\r\n                            if (cpt.equals(\"90712\")) {\r\n                                polio++;\r\n                                polioTime = proc.getDate().getTime();\r\n                            } else// Measles, Mumps, Rubella (90707)\r\n\r\n                                if (cpt.equals(\"90707\")) {\r\n                                    measles++;\r\n                                    measlesTime = proc.getDate().getTime();\r\n                                } else// Varicella (90396)\r\n\r\n                                    if (cpt.equals(\"90396\")) {\r\n                                        varicella++;\r\n                                        varicellaTime = proc.getDate().getTime();\r\n                                    } else// Hep A (90633)\r\n\r\n                                        if (cpt.equals(\"90633\")) {\r\n                                            hepA++;\r\n                                            hepATime = proc.getDate().getTime();\r\n                                        } else// Human Papillomaavirus (90649)\r\n\r\n                                            if (cpt.equals(\"90649\")) {\r\n                                                hpv++;\r\n                                                hpvTime = proc.getDate().getTime();\r\n                                            }\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n    }\r\n    if (3 > hepB) {\r\n        reason += GetVisitRemindersAction.testHepB(hepB, patientDOB, hepBTime);\r\n    }\r\n    if (3 > rota) {\r\n        reason += GetVisitRemindersAction.testRotaVirus(rota, patientDOB, rotaTime);\r\n    }\r\n    if (6 > diptet) {\r\n        reason += GetVisitRemindersAction.testDipTet(diptet, patientDOB, deptetTime);\r\n    }\r\n    if (3 > haemoflu) {\r\n        reason += GetVisitRemindersAction.testHaemoFlu(haemoflu, patientDOB, haemofluTime, haemofluTimeFirst);\r\n    }\r\n    if (4 > pneumo) {\r\n        reason += GetVisitRemindersAction.testPneumo(pneumo, patientDOB, pneumoTime, pneumofluTimeFirst);\r\n    }\r\n    if (3 > polio) {\r\n        reason += GetVisitRemindersAction.testPolio(polio, patientDOB, polioTime);\r\n    }\r\n    if (2 > measles) {\r\n        reason += GetVisitRemindersAction.testMeasles(measles, patientDOB, measlesTime);\r\n    }\r\n    if (2 > varicella) {\r\n        reason += GetVisitRemindersAction.testVaricella(varicella, patientDOB, varicellaTime);\r\n    }\r\n    if (2 > hepA) {\r\n        reason += GetVisitRemindersAction.testHepA(hepA, patientDOB, hepATime);\r\n    }\r\n    if ((3 > hpv) && (gen.getName().equals(\"Female\"))) {\r\n        reason += GetVisitRemindersAction.testHPV(hpv, patientDOB, hpvTime);\r\n    }\r\n    if ((reason.length()) > 2)\r\n        return reason.substring(0, ((reason.length()) - 2));\r\n\r\n    return reason;\r\n}'),(587,'endBefore(java.util.Date,java.util.Date,int,int,int)','edu.ncsu.csc.itrust.action.GetVisitRemindersAction.endBefore(java.util.Date,java.util.Date,int,int,int)','109','edu.ncsu.csc.itrust.action.GetVisitRemindersAction','private static boolean endBefore(Date startTime, Date endTime, int years, int months, int weeks) {\r\n    Calendar cal = Calendar.getInstance();\r\n    cal.setTime(startTime);\r\n    cal.add(Calendar.YEAR, years);\r\n    cal.add(Calendar.MONTH, months);\r\n    cal.add(Calendar.HOUR, ((weeks * 7) * 24));\r\n    return (cal.getTime().compareTo(endTime)) <= 0;// startTime.before(endTime);\r\n\r\n}'),(588,'firstDoseAfter(java.util.Date,java.util.Date,int,int,int)','edu.ncsu.csc.itrust.action.GetVisitRemindersAction.firstDoseAfter(java.util.Date,java.util.Date,int,int,int)','109','edu.ncsu.csc.itrust.action.GetVisitRemindersAction','private static boolean firstDoseAfter(Date patientDOB, Date ageFirst, int years, int months, int weeks) {\r\n    return GetVisitRemindersAction.endBefore(patientDOB, ageFirst, years, months, weeks);\r\n}'),(589,'getImmunizationNeeders(long)','edu.ncsu.csc.itrust.action.GetVisitRemindersAction.getImmunizationNeeders(long)','109','edu.ncsu.csc.itrust.action.GetVisitRemindersAction','/**\r\n * Gets a list of anyone who need immunizations\r\n *\r\n * @param mid\r\n * 		the HCP whose patients are being checked\r\n * @return a list of all the people who need immunizations--done in a visit reminder\r\n * @throws iTrustException\r\n * 		\r\n */\r\nprivate List<VisitReminderReturnForm> getImmunizationNeeders(long mid) throws iTrustException {\r\n    List<VisitReminderReturnForm> formList;\r\n    List<VisitReminderReturnForm> needList = new ArrayList<VisitReminderReturnForm>();\r\n    String reason = \"\";\r\n    // Get list of patients that designate this HCP\r\n    formList = visitReminderDAO.getPatients(mid);\r\n    for (VisitReminderReturnForm r : formList) {\r\n        reason = checkImmunizations(r.getPatientID());\r\n        if (0 < (reason.length())) {\r\n            needList.add(r);\r\n            r.addVisitFlag(new VisitFlag(VisitFlag.IMMUNIZATION, reason));\r\n        }\r\n    }\r\n    return needList;\r\n}'),(590,'getVisitReminders(edu.ncsu.csc.itrust.action.GetVisitRemindersAction$ReminderType)','edu.ncsu.csc.itrust.action.GetVisitRemindersAction.getVisitReminders(edu.ncsu.csc.itrust.action.GetVisitRemindersAction$ReminderType)','109','edu.ncsu.csc.itrust.action.GetVisitRemindersAction','/**\r\n * Returns a list of VisitReminderReturnForms for the type passed in as a param\r\n *\r\n * @param type\r\n * 		the ReminderType\r\n * @return the list of VisitReminderReturnForms\r\n * @throws iTrustException\r\n * 		\r\n * @throws FormValidationException\r\n * 		\r\n */\r\npublic List<VisitReminderReturnForm> getVisitReminders(GetVisitRemindersAction.ReminderType type) throws FormValidationException, iTrustException {\r\n    if (null == type)\r\n        throw new iTrustException(\"Reminder Type DNE\");\r\n\r\n    switch (type) {\r\n        case DIAGNOSED_CARE_NEEDERS :\r\n            return visitReminderDAO.getDiagnosedVisitNeeders(loggedInMID);\r\n            // return stripDupes(visitReminderDAO.getDiagnosedVisitNeeders(loggedInMID));\r\n        case FLU_SHOT_NEEDERS :\r\n            return visitReminderDAO.getFluShotDelinquents(loggedInMID);\r\n        case IMMUNIZATION_NEEDERS :\r\n            return getImmunizationNeeders(loggedInMID);\r\n        default :\r\n            throw new iTrustException(\"Reminder Type DNE\");\r\n    }\r\n}'),(591,'olderThan(java.util.Date,int,int,int)','edu.ncsu.csc.itrust.action.GetVisitRemindersAction.olderThan(java.util.Date,int,int,int)','109','edu.ncsu.csc.itrust.action.GetVisitRemindersAction','private static boolean olderThan(Date patientDOB, int years, int months, int weeks) {\r\n    return GetVisitRemindersAction.endBefore(patientDOB, new Date(), years, months, weeks);\r\n}'),(592,'testDipTet(int,java.util.Date,long)','edu.ncsu.csc.itrust.action.GetVisitRemindersAction.testDipTet(int,java.util.Date,long)','109','edu.ncsu.csc.itrust.action.GetVisitRemindersAction','/**\r\n * Checks to see if a patient needs the Diphtheria, Tetanus, Pertussis immunization\r\n *\r\n * @param count\r\n * 		which immunization they are on\r\n * @param patientAge\r\n * 		how old the patient is\r\n * @param time\r\n * 		what the current date is\r\n * @return when the immunization should be given\r\n */\r\npublic static String testDipTet(int count, Date patientDOB, long time) {\r\n    String reason = \"\";\r\n    long weeks = ((Calendar.getInstance().getTimeInMillis()) - time) / (GetVisitRemindersAction.WEEK_IN_MILLIS);\r\n    if (0 == count) {\r\n        if (GetVisitRemindersAction.olderThan(patientDOB, 0, 0, 6))\r\n            reason += \"90696 Diphtheria, Tetanus, Pertussis (6 weeks), \";\r\n\r\n    } else\r\n        if (1 == count) {\r\n            if ((GetVisitRemindersAction.olderThan(patientDOB, 0, 4, 0)) && (4 <= weeks))\r\n                reason += \"90696 Diphtheria, Tetanus, Pertussis (4 months), \";\r\n\r\n        } else\r\n            if (2 == count) {\r\n                if ((GetVisitRemindersAction.olderThan(patientDOB, 0, 6, 0)) && (4 <= weeks))\r\n                    reason += \"90696 Diphtheria, Tetanus, Pertussis (6 months), \";\r\n\r\n            } else\r\n                if (3 == count) {\r\n                    if ((GetVisitRemindersAction.olderThan(patientDOB, 0, 0, 15)) && (26 <= weeks))\r\n                        reason += \"90696 Diphtheria, Tetanus, Pertussis (15 weeks), \";\r\n\r\n                } else\r\n                    if (4 == count) {\r\n                        if ((GetVisitRemindersAction.olderThan(patientDOB, 4, 0, 0)) && (26 <= weeks))\r\n                            reason += \"90696 Diphtheria, Tetanus, Pertussis (4 years), \";\r\n\r\n                    } else\r\n                        if (5 == c