;\r\n    msg.setSubject(\"Your appointment request\");\r\n    msg.setSentDate(new Timestamp(System.currentTimeMillis()));\r\n    String body = (((\"Your appointment request with \" + (pnDAO.getName(appt.getHcp()))) + \" on \") + (appt.getDate())) + \" has been \";\r\n    if (accepted)\r\n        body += \"accepted.\";\r\n    else\r\n        body += \"rejected.\";\r\n\r\n    msg.setBody(body);\r\n    return msg;\r\n}'),(992,'getApptRequests()','edu.ncsu.csc.itrust.action.ViewApptRequestsAction.getApptRequests()','162','edu.ncsu.csc.itrust.action.ViewApptRequestsAction','public List<ApptRequestBean> getApptRequests() throws SQLException {\r\n    List<ApptRequestBean> reqs = arDAO.getApptRequestsFor(hcpid);\r\n    return reqs;\r\n}'),(993,'getNumRequests(java.util.List)','edu.ncsu.csc.itrust.action.ViewApptRequestsAction.getNumRequests(java.util.List)','162','edu.ncsu.csc.itrust.action.ViewApptRequestsAction','/**\r\n *\r\n *\r\n * @param reqs\r\n * 		\r\n * @return int\r\n\r\nReturns the number of times in the appointment request list\r\n */\r\npublic int getNumRequests(List<ApptRequestBean> reqs) {\r\n    int numOfPendingAppointments = 0;\r\n    for (int i = 0; i < (reqs.size()); i++) {\r\n        if ((reqs.get(i).isPending()) == true) {\r\n            numOfPendingAppointments++;\r\n        }\r\n    }\r\n    return numOfPendingAppointments;\r\n}'),(994,'rejectApptRequest(int)','edu.ncsu.csc.itrust.action.ViewApptRequestsAction.rejectApptRequest(int)','162','edu.ncsu.csc.itrust.action.ViewApptRequestsAction','public String rejectApptRequest(int reqID) throws SQLException {\r\n    ApptRequestBean req = arDAO.getApptRequest(reqID);\r\n    if ((req.isPending()) && (!(req.isAccepted()))) {\r\n        req.setPending(false);\r\n        req.setAccepted(false);\r\n        arDAO.updateApptRequest(req);\r\n        try {\r\n            MessageBean msg = constructMessage(req.getRequestedAppt(), req.isAccepted());\r\n            msgAction.sendMessage(msg);\r\n        } catch (Exception e) {\r\n        }\r\n        return \"The appointment request you selected has been rejected.\";\r\n    } else {\r\n        return \"The appointment request you selected has already been acted upon.\";\r\n    }\r\n}'),(995,'-init-()','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest.-init-()','163','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest','ViewApptRequestsActionTest() {\r\n}'),(996,'setUp()','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest.setUp()','163','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest','protected void setUp() throws Exception {\r\n    gen.clearAllTables();\r\n    gen.standardData();\r\n    gen.apptRequestConflicts();\r\n    action = new ViewApptRequestsAction(9000000000L, TestDAOFactory.getTestInstance());\r\n    mDAO = TestDAOFactory.getTestInstance().getMessageDAO();\r\n}'),(997,'testAcceptApptRequest()','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest.testAcceptApptRequest()','163','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest','public void testAcceptApptRequest() throws Exception {\r\n    List<ApptRequestBean> list = action.getApptRequests();\r\n    assertEquals(1, list.size());\r\n    assertEquals(2L, list.get(0).getRequestedAppt().getPatient());\r\n    String res = action.acceptApptRequest(list.get(0).getRequestedAppt().getApptID());\r\n    assertEquals(\"The appointment request you selected has been accepted and scheduled.\", res);\r\n    list = action.getApptRequests();\r\n    assertEquals(1, list.size());\r\n    assertEquals(2L, list.get(0).getRequestedAppt().getPatient());\r\n    assertTrue(list.get(0).isAccepted());\r\n    List<MessageBean> msgs = mDAO.getMessagesFor(list.get(0).getRequestedAppt().getPatient());\r\n    assertEquals(list.get(0).getRequestedAppt().getHcp(), msgs.get(0).getFrom());\r\n    assertTrue(msgs.get(0).getBody().contains(\"has been accepted.\"));\r\n}'),(998,'testGetApptRequests()','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest.testGetApptRequests()','163','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest','public void testGetApptRequests() throws Exception {\r\n    List<ApptRequestBean> list = action.getApptRequests();\r\n    assertEquals(1, list.size());\r\n    assertEquals(2L, list.get(0).getRequestedAppt().getPatient());\r\n}'),(999,'testRejectApptRequest()','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest.testRejectApptRequest()','163','edu.ncsu.csc.itrust.action.ViewApptRequestsActionTest','public void testRejectApptRequest() throws Exception {\r\n    List<ApptRequestBean> list = action.getApptRequests();\r\n    assertEquals(1, list.size());\r\n    assertEquals(2L, list.get(0).getRequestedAppt().getPatient());\r\n    String res = action.rejectApptRequest(list.get(0).getRequestedAppt().getApptID());\r\n    assertEquals(\"The appointment request you selected has been rejected.\", res);\r\n    list = action.getApptRequests();\r\n    assertEquals(1, list.size());\r\n    assertEquals(2L, list.get(0).getRequestedAppt().getPatient());\r\n    assertFalse(list.get(0).isAccepted());\r\n    List<MessageBean> msgs = mDAO.getMessagesFor(list.get(0).getRequestedAppt().getPatient());\r\n    assertEquals(list.get(0).getRequestedAppt().getHcp(), msgs.get(0).getFrom());\r\n    assertTrue(msgs.get(0).getBody().contains(\"has been rejected.\"));\r\n}'),(1000,'-init-(edu.ncsu.csc.itrust.dao.DAOFactory)','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction.-init-(edu.ncsu.csc.itrust.dao.DAOFactory)','164','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction','/**\r\n * Constructor for the action. Initializes DAO fields\r\n *\r\n * @param factory\r\n * 		The sessions factory for DAOs\r\n */\r\npublic ViewDiagnosisStatisticsAction(DAOFactory factory) {\r\n    this.icdDAO = factory.getICDCodesDAO();\r\n    this.diagnosesDAO = factory.getDiagnosesDAO();\r\n}'),(1001,'calcThreshold(double)','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction.calcThreshold(double)','164','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction','/**\r\n * Calculates the threshold of an influenza epidemic\r\n *\r\n * @param weekNumber\r\n * 		the week of the year\r\n * @return the epidemic threshold for flu cases\r\n */\r\nprivate double calcThreshold(double weekNumber) {\r\n    return ((5.34 + (0.271 * weekNumber)) + (3.45 * (Math.sin((((2 * (Math.PI)) * weekNumber) / 52.0))))) + (8.41 * (Math.cos((((2 * (Math.PI)) * weekNumber) / 52.0))));\r\n}'),(1002,'getDiagnosisCodes()','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction.getDiagnosisCodes()','164','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction','/**\r\n * Gets all the diagnosis codes in iTrust and returns them in a list of beans.\r\n *\r\n * @return List of DiagnosisBeans correlating to all ICDCodes\r\n * @throws DBException\r\n * 		\r\n * @throws iTrustException\r\n * 		\r\n */\r\npublic List<DiagnosisBean> getDiagnosisCodes() throws DBException, iTrustException {\r\n    return icdDAO.getAllICDCodes();\r\n}'),(1003,'getDiagnosisStatistics(java.lang.String,java.lang.String,java.lang.String,java.lang.String)','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction.getDiagnosisStatistics(java.lang.String,java.lang.String,java.lang.String,java.lang.String)','164','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction','/**\r\n * Gets the counts of local and regional diagnoses for the specified input\r\n *\r\n * @param lowerDate\r\n * 		The beginning date for the time range\r\n * @param upperDate\r\n * 		The ending date for the time range\r\n * @param icdCode\r\n * 		The diagnosis code to examine\r\n * @param zip\r\n * 		The zip code to examine\r\n * @return A bean containing the local and regional counts\r\n * @throws FormValidationException\r\n * 		\r\n * @throws DBException\r\n * 		\r\n * @throws iTrustException\r\n * 		\r\n */\r\npublic DiagnosisStatisticsBean getDiagnosisStatistics(String lowerDate, String upperDate, String icdCode, String zip) throws DBException, FormValidationException, iTrustException {\r\n    DiagnosisStatisticsBean dsBean;\r\n    try {\r\n        if (((lowerDate == null) || (upperDate == null)) || (icdCode == null))\r\n            return null;\r\n\r\n        Date lower = new SimpleDateFormat(\"MM/dd/yyyy\").parse(lowerDate);\r\n        Date upper = new SimpleDateFormat(\"MM/dd/yyyy\").parse(upperDate);\r\n        if (lower.after(upper))\r\n            throw new FormValidationException(\"Start date must be before end date!\");\r\n\r\n        if (!(zip.matches(\"([0-9]{5})|([0-9]{5}-[0-9]{4})\")))\r\n            throw new FormValidationException(\"Zip Code must be 5 digits!\");\r\n\r\n        boolean validCode = false;\r\n        for (DiagnosisBean diag : getDiagnosisCodes()) {\r\n            if (diag.getICDCode().equals(icdCode))\r\n                validCode = true;\r\n\r\n        }\r\n        if (validCode == false) {\r\n            throw new FormValidationException(\"ICDCode must be valid diagnosis!\");\r\n        }\r\n        dsBean = diagnosesDAO.getDiagnosisCounts(icdCode, zip, lower, upper);\r\n    } catch (ParseException e) {\r\n        throw new FormValidationException(\"Enter dates in MM/dd/yyyy\");\r\n    }\r\n    return dsBean;\r\n}'),(1004,'getEpidemicStatistics(java.lang.String,java.lang.String,java.lang.String,java.lang.String)','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction.getEpidemicStatistics(java.lang.String,java.lang.String,java.lang.String,java.lang.String)','164','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction','/**\r\n * Gets the local and regional counts for the specified week and calculates the prior average.\r\n *\r\n * @param startDate\r\n * 		a date in the week to analyze\r\n * @param icdCode\r\n * 		the diagnosis to analyze\r\n * @param zip\r\n * 		the area to analyze\r\n * @param threshold\r\n * 		\r\n * @return statistics for the week and previous averages\r\n * @throws FormValidationException\r\n * 		\r\n * @throws DBException\r\n * 		\r\n */\r\npublic ArrayList<DiagnosisStatisticsBean> getEpidemicStatistics(String startDate, String icdCode, String zip, String threshold) throws DBException, FormValidationException {\r\n    if ((startDate == null) || (icdCode == null))\r\n        return null;\r\n\r\n    if (!((icdCode.equals(\"84.50\")) || (icdCode.equals(\"487.00\")))) {\r\n        throw new FormValidationException(\"Exception\");\r\n    }\r\n    if (ViewDiagnosisStatisticsAction.ICD_MALARIA.equals(icdCode)) {\r\n        try {\r\n            Integer.parseInt(threshold);\r\n        } catch (NumberFormatException e) {\r\n            throw new FormValidationException(\"Threshold must be an integer.\");\r\n        }\r\n    }\r\n    Date lower;// lower, which is parsed to startDate\r\n\r\n    try {\r\n        lower = new SimpleDateFormat(\"MM/dd/yyyy\").parse(startDate);\r\n    } catch (ParseException e) {\r\n        throw new FormValidationException(\"Enter dates in MM/dd/yyyy\");\r\n    }\r\n    if (!(zip.matches(\"([0-9]{5})|([0-9]{5}-[0-9]{4})\")))\r\n        throw new FormValidationException(\"Zip Code must be 5 digits!\");\r\n\r\n    DiagnosisStatisticsBean dbWeek = diagnosesDAO.getCountForWeekOf(icdCode, zip, lower);\r\n    DiagnosisStatisticsBean dbAvg = new DiagnosisStatisticsBean(zip, 0, 0, lower, lower);\r\n    Calendar cal = Calendar.getInstance();\r\n    Date start = diagnosesDAO.findEarliestIncident(icdCode);// start, which is set to earliest incident\r\n\r\n    Calendar startCal = Calendar.getInstance();\r\n    startCal.setTime(start);\r\n    ArrayList<DiagnosisStatisticsBean> ret = new ArrayList<DiagnosisStatisticsBean>();\r\n    if (start == null) {\r\n        ret.add(dbWeek);\r\n        ret.add(dbAvg);\r\n        return ret;\r\n    }\r\n    cal.setTime(lower);// cal, which is set to lower\r\n\r\n    Calendar lowerCal = Calendar.getInstance();\r\n    lowerCal.setTime(lower);\r\n    int weekOfYr = cal.get(Calendar.WEEK_OF_YEAR);\r\n    cal.set(Calendar.YEAR, startCal.get(Calendar.YEAR));// cals year then gets set to starts year\r\n\r\n    ArrayList<DiagnosisStatisticsBean> dbList = new ArrayList<DiagnosisStatisticsBean>();\r\n    while ((cal.getTime().before(lower)) && ((cal.get(Calendar.YEAR)) != (lowerCal.get(Calendar.YEAR)))) {\r\n        dbList.add(diagnosesDAO.getCountForWeekOf(icdCode, zip, cal.getTime()));\r\n        cal.add(Calendar.YEAR, 1);\r\n        cal.set(Calendar.WEEK_OF_YEAR, weekOfYr);\r\n        cal.set(Calendar.DAY_OF_WEEK, Calendar.MONDAY);\r\n    } \r\n    long avg = 0;\r\n    long avgRegion = 0;\r\n    if ((dbList.size()) > 0) {\r\n        for (DiagnosisStatisticsBean d : dbList) {\r\n            avg += d.getZipStats();\r\n            avgRegion += d.getRegionStats();\r\n        }\r\n        avg /= dbList.size();\r\n        avgRegion /= dbList.size();\r\n    }\r\n    dbAvg.setRegionStats(avgRegion);\r\n    dbAvg.setZipStats(avg);\r\n    ret.add(dbWeek);\r\n    ret.add(dbAvg);\r\n    return ret;\r\n}'),(1005,'isFluEpidemic(java.lang.String,java.lang.String)','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction.isFluEpidemic(java.lang.String,java.lang.String)','164','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction','/**\r\n * Determines if an Influenza Epidemic is happening\r\n *\r\n * @param curDateStr\r\n * 		a date in the currently evaluated week\r\n * @param zip\r\n * 		the zip code to analyze\r\n * @return whether or not there is an epidemic\r\n * @throws ParseException\r\n * 		\r\n * @throws DBException\r\n * 		\r\n */\r\npublic boolean isFluEpidemic(String curDateStr, String zip) throws DBException, ParseException {\r\n    new SimpleDateFormat(\"MM/dd/yyyy\").parse(\"01/04/1998\");\r\n    Date curDate = new SimpleDateFormat(\"MM/dd/yyyy\").parse(curDateStr);\r\n    Calendar cal = Calendar.getInstance();\r\n    cal.setTime(curDate);\r\n    int weekOfYr = cal.get(Calendar.WEEK_OF_YEAR);\r\n    double threshold = calcThreshold(weekOfYr);\r\n    double thresholdL = calcThreshold((weekOfYr - 1));\r\n    double thresholdN = calcThreshold((weekOfYr + 1));\r\n    DiagnosisStatisticsBean dbNow = diagnosesDAO.getCountForWeekOf(ViewDiagnosisStatisticsAction.ICD_INFLUENZA, zip, cal.getTime());\r\n    cal.add(Calendar.HOUR, ((-12) * 7));\r\n    DiagnosisStatisticsBean dbLast = diagnosesDAO.getCountForWeekOf(ViewDiagnosisStatisticsAction.ICD_INFLUENZA, zip, cal.getTime());\r\n    cal.add(Calendar.HOUR, ((2 * 12) * 7));\r\n    DiagnosisStatisticsBean dbNext = diagnosesDAO.getCountForWeekOf(ViewDiagnosisStatisticsAction.ICD_INFLUENZA, zip, cal.getTime());\r\n    double weekNow = ((double) (dbNow.getRegionStats()));\r\n    double weekL = ((double) (dbLast.getRegionStats()));\r\n    double weekN = ((double) (dbNext.getRegionStats()));\r\n    if ((weekNow > threshold) && ((weekL > thresholdL) || (weekN > thresholdN))) {\r\n        return true;\r\n    } else {\r\n        return false;\r\n    }\r\n}'),(1006,'isMalariaEpidemic(java.lang.String,java.lang.String,java.lang.String)','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction.isMalariaEpidemic(java.lang.String,java.lang.String,java.lang.String)','164','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsAction','/**\r\n * Determines whether a Malaria epidemic is happening\r\n *\r\n * @param weekDate\r\n * 		a date in the currently evaluated week\r\n * @param zip\r\n * 		the zip code to analyze\r\n * @param thresholdStr\r\n * 		the threshold for an epidemic\r\n * @return whether or not there is an epidemic\r\n * @throws DBException\r\n * 		\r\n * @throws ParseException\r\n * 		\r\n */\r\npublic boolean isMalariaEpidemic(String weekDate, String zip, String thresholdStr) throws DBException, ParseException {\r\n    Date wkDate = new SimpleDateFormat(\"MM/dd/yyyy\").parse(weekDate);\r\n    ArrayList<DiagnosisStatisticsBean> dbList = new ArrayList<DiagnosisStatisticsBean>();\r\n    ArrayList<DiagnosisStatisticsBean> dbListL = new ArrayList<DiagnosisStatisticsBean>();\r\n    ArrayList<DiagnosisStatisticsBean> dbListN = new ArrayList<DiagnosisStatisticsBean>();\r\n    int threshold = Integer.parseInt(thresholdStr);\r\n    DiagnosisStatisticsBean current = diagnosesDAO.getCountForWeekOf(ViewDiagnosisStatisticsAction.ICD_MALARIA, zip, wkDate);\r\n    long weekTotal = current.getRegionStats();\r\n    Calendar cal = Calendar.getInstance();\r\n    cal.setTime(wkDate);\r\n    cal.add(Calendar.HOUR, ((-7) * 24));\r\n    DiagnosisStatisticsBean last = diagnosesDAO.getCountForWeekOf(ViewDiagnosisStatisticsAction.ICD_MALARIA, zip, cal.getTime());\r\n    long weekTotalL = last.getRegionStats();\r\n    cal.add(Calendar.HOUR, ((2 * 7) * 24));\r\n    DiagnosisStatisticsBean next = diagnosesDAO.getCountForWeekOf(ViewDiagnosisStatisticsAction.ICD_MALARIA, zip, cal.getTime());\r\n    long weekTotalN = next.getRegionStats();\r\n    cal.setTime(wkDate);\r\n    int weekOfYr = cal.get(Calendar.WEEK_OF_YEAR);\r\n    // Find earliest Malaria Case. Set calendars year to that year\r\n    Date startData = diagnosesDAO.findEarliestIncident(ViewDiagnosisStatisticsAction.ICD_MALARIA);\r\n    if (startData == null) {\r\n        if ((current.getRegionStats()) > 0) {\r\n            return true;\r\n        }\r\n        return false;\r\n    }\r\n    Calendar startDateCal = Calendar.getInstance();\r\n    startDateCal.setTime(startData);\r\n    Calendar wkDateCal = Calendar.getInstance();\r\n    wkDateCal.setTime(wkDate);\r\n    cal.set(Calendar.YEAR, startDateCal.get(Calendar.YEAR));\r\n    while ((cal.getTime().before(wkDate)) && ((cal.get(Calendar.YEAR)) != (wkDateCal.get(Calendar.YEAR)))) {\r\n        dbList.add(diagnosesDAO.getCountForWeekOf(ViewDiagnosisStatisticsAction.ICD_MALARIA, zip, cal.getTime()));\r\n        cal.add(Calendar.HOUR, ((-7) * 24));\r\n        dbListL.add(diagnosesDAO.getCountForWeekOf(ViewDiagnosisStatisticsAction.ICD_MALARIA, zip, cal.getTime()));\r\n        cal.add(Calendar.HOUR, ((2 * 7) * 24));\r\n        dbListN.add(diagnosesDAO.getCountForWeekOf(ViewDiagnosisStatisticsAction.ICD_MALARIA, zip, cal.getTime()));\r\n        cal.add(Calendar.YEAR, 1);\r\n        cal.set(Calendar.WEEK_OF_YEAR, weekOfYr);\r\n        cal.set(Calendar.DAY_OF_WEEK, Calendar.MONDAY);\r\n    } \r\n    long total = 0;\r\n    for (DiagnosisStatisticsBean d : dbList) {\r\n        total += d.getRegionStats();\r\n    }\r\n    for (DiagnosisStatisticsBean d : dbListL) {\r\n        d.getRegionStats();\r\n    }\r\n    for (DiagnosisStatisticsBean d : dbListN) {\r\n        d.getRegionStats();\r\n    }\r\n    long avg = 0;\r\n    long avgL = 0;\r\n    long avgN = 0;\r\n    if ((dbList.size()) != 0) {\r\n        avg = total / (dbList.size());\r\n        avgL = total / (dbListL.size());\r\n        avgN = total / (dbListN.size());\r\n    }\r\n    if (((weekTotal != 0) && (((weekTotal * 100) / threshold) > avg)) && (((weekTotalL != 0) && (((weekTotalL * 100) / threshold) > avgL)) || ((weekTotalN != 0) && (((weekTotalN * 100) / threshold) > avgN)))) {\r\n        return true;\r\n    } else {\r\n        return false;\r\n    }\r\n}'),(1007,'-init-()','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsActionTest.-init-()','165','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsActionTest','ViewDiagnosisStatisticsActionTest() {\r\n}'),(1008,'setUp()','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsActionTest.setUp()','165','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsActionTest','protected void setUp() throws Exception {\r\n    gen.clearAllTables();\r\n    gen.standardData();\r\n    gen.patient_hcp_vists();\r\n    gen.hcp_diagnosis_data();\r\n    action = new ViewDiagnosisStatisticsAction(TestDAOFactory.getTestInstance());\r\n}'),(1009,'testGetDiagnosisCodes()','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsActionTest.testGetDiagnosisCodes()','165','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisticsActionTest','public void testGetDiagnosisCodes() throws Exception {\r\n    List<DiagnosisBean> db = action.getDiagnosisCodes();\r\n    assertEquals(15, db.size());\r\n}'),(1010,'testGetDiagnosisStatisticsInvalidDate()','edu.ncsu.csc.itrust.action.ViewDiagnosisStatisti