        if (lower.after(upper))\r\n            throw new FormValidationException(\"Start date must be before end date!\");\r\n\r\n    } catch (ParseException e) {\r\n        throw new FormValidationException(\"Enter dates in MM/dd/yyyy\");\r\n    }\r\n    return rmDAO.getPatientDataByDate(patientMID, lower, upper);\r\n}'),(1159,'getPatientDataByType(long,java.lang.String)','edu.ncsu.csc.itrust.action.ViewMyRemoteMonitoringListAction.getPatientDataByType(long,java.lang.String)','180','edu.ncsu.csc.itrust.action.ViewMyRemoteMonitoringListAction','/**\r\n * Returns a list of RemoteMonitoringDataBeans for the logged in HCP based on a certain data type\r\n *\r\n * @param patientMID\r\n * 		\r\n * @param dataType\r\n * 		\r\n * @return list of TransactionBeans\r\n * @throws DBException\r\n * 		\r\n * @throws FormValidationException\r\n * 		\r\n */\r\npublic List<RemoteMonitoringDataBean> getPatientDataByType(long patientMID, String dataType) throws DBException, FormValidationException {\r\n    String[] types = new String[]{ \"weight\", \"systolicBloodPressure\", \"diastolicBloodPressure\", \"glucoseLevel\", \"pedometerReading\" };\r\n    boolean valid = false;\r\n    for (String dType : types) {\r\n        if (dType.equals(dataType)) {\r\n            valid = true;\r\n            break;\r\n        }\r\n    }\r\n    if (!valid) {\r\n        throw new FormValidationException(\"Input must be a valid telemedicine data type!\");\r\n    }\r\n    return rmDAO.getPatientDataByType(patientMID, dataType);\r\n}'),(1160,'getPatientDataWithoutLogging()','edu.ncsu.csc.itrust.action.ViewMyRemoteMonitoringListAction.getPatientDataWithoutLogging()','180','edu.ncsu.csc.itrust.action.ViewMyRemoteMonitoringListAction','public List<RemoteMonitoringDataBean> getPatientDataWithoutLogging() throws DBException {\r\n    return rmDAO.getPatientsData(loggedInMID);\r\n}'),(1161,'getPatientName(long)','edu.ncsu.csc.itrust.action.ViewMyRemoteMonitoringListAction.getPatientName(long)','180','edu.ncsu.csc