ndEarliestIncident(String icdCode) throws DBException {\r\n    Connection conn = null;\r\n    PreparedStatement ps = null;\r\n    try {\r\n        conn = factory.getConnection();\r\n        ps = conn.prepareStatement((\"SELECT MIN(visitDate) AS visitDate FROM ovdiagnosis d INNER JOIN officevisits o \" + (\" ON d.VisitID=o.ID \" + \" WHERE ICDCode LIKE ?\")));\r\n        ps.setString(1, (icdCode + \"%\"));\r\n        ResultSet rs = ps.executeQuery();\r\n        if (rs.next()) {\r\n            Date date = rs.getDate(\"visitDate\");\r\n            return date;\r\n        }\r\n    } catch (SQLException e) {\r\n        e.printStackTrace();\r\n        throw new DBException(e);\r\n    } finally {\r\n        DBUtil.closeConnection(conn, ps);\r\n    }\r\n    return null;\r\n}'),(2880,'getCountForWeekOf(java.lang.String,java.lang.String,java.util.Date)','edu.ncsu.csc.itrust.dao.mysql.DiagnosesDAO.getCountForWeekOf(java.lang.String,java.lang.String,java.util.Date)','389','edu.ncsu.csc.itrust.dao.mysql.DiagnosesDAO','public DiagnosisStatisticsBean getCountForWeekOf(String icdCode, String zipCode, Date lower) throws DBException {\r\n    Calendar cal = Calendar.getInstance();\r\n    cal.setTime(lower);\r\n    cal.set(Calendar.DAY_OF_WEEK, Calendar.MONDAY);\r\n    Date lowerDate = cal.getTime();\r\n    cal.add(Calendar.HOUR, (24 * 6));\r\n    Date upperDate = cal.getTime();\r\n    return getDiagnosisCounts(icdCode, zipCode, lowerDate, upperDate);\r\n}'),(2881,'getDiagnosisCounts(java.lang.String,java.lang.String,java.util.Date,java.util.Date)','edu.ncsu.csc.itrust.dao.mysql.DiagnosesDAO.getDiagnosisCounts(java.lang.String,java.lang.String,java.util.Date,java.util.Date)','389','edu.ncsu.csc.itrust.dao.mysql.DiagnosesDAO','/**\r\n * Gets a local zip code count and regional count of a specified diagnosis code\r\n *\r\n * @param icdCode\r\n * 		The diagnosis code\r\n * @param zipCode\r\n * 		The zip code to evaluate\r\n * @param lower\r\n * 		The starting date\r\n * @param upper\r\n *